<?phprcl_enqueue_style('zoom-image',__FILE__);// подключаю скрипт. Поддерживаю объединение скриптовfunction zoomin_initiale() {    if(!is_admin()){ // в админке он нам не нужен        global $active_addons, $rcl_options;        // если активен аддон галлерея и стоит в нем использовать fancybox - заменяем стиль и скрипты        if ($active_addons['gallery-recall'] && $rcl_options['fancybox_grcl'] == 1) {            wp_deregister_style('grcl_fancybox_style');            wp_deregister_script('grcl_mousewheel');            wp_deregister_script('grcl_fancybox');            rcl_enqueue_script('zoomin_fancybox', rcl_addon_url('incl/my-fancybx.js', __FILE__)); // подключаю свой        } else {            rcl_enqueue_script('zoomin_fancybox', rcl_addon_url('incl/my-fancybx.js', __FILE__));        }    }}add_action('rcl_enqueue_scripts','zoomin_initiale',10);// подключаю инициализацию в футер, в хедере у нас основной fancybx грузится. Поддерживается объединение скриптовfunction zooim_script_load() {    if(!is_admin()){        rcl_enqueue_script('zoomin_fancybox_init', rcl_addon_url('incl/fancybx-init.js', __FILE__),false,true);    }}add_action('rcl_enqueue_scripts', 'zooim_script_load',10);